(.setSourceCode "\\newlinefunction initialize () // CameraScript\\newline{\\newline//  tracePrint ('initialize start...');\\newline\\newline    NavInfoNode.avatarSize[0]   = nearClipPlane;\\newline\\newline    // remaining setups deferred to invocation of checkShots() method\\newline    // thanks to Yvonne Jung Fraunhofer for diagnosing better approach to function initialization\\newline    alwaysPrint ('initialize complete');\\newline}\\newline\\newlinefunction checkShots (eventValue)\\newline{\\newline    tracePrint ('checkShots() method should only occur after initialize() methods in all other Scripts are complete');\\newline\\newline    // compute totalDuration by summing durations from contained CameraShot and CameraMovement nodes\\newline    totalDuration= 0;\\newline    shotCount  = shots.length;\\newline    movesCount = 0;\\newline    for (i = 0; i < shotCount; i++) // shots index\\newline    {\\newline       tracePrint ('shots[' + i + '].moves.length=' + shots[i].moves.length);\\newline       movesCount   += shots[i].moves.length;\\newline       totalDuration = totalDuration + shots[i].shotDuration;\\newline       if (shots[i].moves.length == 0)\\newline       {\\newline          alwaysPrint ('warning: CameraShot[' + i + '][' + shots[i].description + '] has no contained CameraMove nodes');\\newline       }\\newline    }\\newline    // size checks before proceeding\\newline    if (shotCount == 0)\\newline    {\\newline       alwaysPrint ('warning: no CameraShot nodes found for the shots, nothing to do!');\\newline       return;\\newline    }\\newline    else if (movesCount == 0)\\newline    {\\newline       alwaysPrint ('warning: no CameraMove nodes found for the shots, nothing to do!');\\newline       return;\\newline    }\\newline    else if (totalDuration == 0)\\newline    {\\newline       alwaysPrint ('warning: totalDuration = 0 seconds, nothing to do!');\\newline       return;\\newline    }\\newline    tracePrint ('number of contained CameraShot nodes=' + shotCount);\\newline    tracePrint ('number of contained CameraMove nodes=' + movesCount);\\newline    tracePrint ('totalDuration=' + totalDuration + ' seconds for all shots and moves');\\newline\\newline    // compute interpolators\\newline    var k = 0; // index for latest key, keyValuePosition, keyValueOrientation\\newline    for (i = 0; i < shotCount; i++) // shots index\\newline    {\\newline        if (i==0) // initial entries\\newline        {\\newline           key[0]                   = 0.0; // no previous move\\newline           keyValuePosition[0]      = shots[i].initialPosition;\\newline           keyValueOrientation[0]   = shots[i].initialOrientation;\\newline        }\\newline        else     // new shot repositions, reorients camera as clean break from preceding shot/move\\newline        {\\newline           key[k+1]                 = key[k]; // start from end from previous move\\newline           keyValuePosition[k+1]    = shots[i].initialPosition;\\newline           keyValueOrientation[k+1] = shots[i].initialOrientation;\\newline           k++;\\newline        }\\newline        tracePrint (shots[i].description);\\newline        tracePrint ('shots[i].moves.length=' + shots[i].moves.length);\\newline\\newline        for (j = 0; j < shots[i].moves.length; j++) // moves index\\newline        {\\newline            var durationFloat =              shots[i].moves[j].duration;  // implicit type conversion from SFTime\\newline            //  durationFloat = new SFFloat (shots[i].moves[j].duration); // explicit type conversion from SFTime\\newline            //  tracePrint ('durationFloat=' + durationFloat);\\newline            key[k+1]               = key[k] + (durationFloat / totalDuration);\\newline            keyValuePosition[k+1]  = shots[i].moves[j].goalPosition;\\newline            if (!animated)\\newline            {\\newline                 keyValueOrientation[k+1] = shots[i].moves[j].goalOrientation;\\newline            }\\newline            else\\newline            {\\newline                // using constructor SFRotation (SFVec3f fromVector, SFVec3f toVector)\\newline                // see X3D ECMAScript binding Table 7.18 â€” SFRotation instance creation functions\\newline\\newline                // test if difference vector is zero, if so maintain previous rotation\\newline                var shotVector = ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).normalize();\\newline                if (shotVector.length() >= 0)\\newline                {\\newline                    // default view direction is along -Z axis\\newline                    shots[i].moves[j].goalOrientation = new SFRotation (new SFVec3f (0, 0, 1), shotVector);\\newline                    keyValueOrientation[k+1] = shots[i].moves[j].goalOrientation;\\newline                }\\newline                else // note (k > 0)\\newline                {\\newline                    keyValueOrientation[k+1] = keyValueOrientation[k];  // no change\\newline                }\\newline\\newline                tracePrint ('shots[' + i + '].moves[' + j + '].goalAimPoint=' + shots[i].moves[j].goalAimPoint.toString());\\newline                tracePrint ('        ViewpointNode.position=' + ViewpointNode.position.toString());\\newline                tracePrint ('          shotVector     delta=' + ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).toString());\\newline                tracePrint ('          shotVector normalize=' + ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).normalize().toString());\\newline                tracePrint ('               goalOrientation=' + shots[i].moves[j].goalOrientation.toString());\\newline                tracePrint ('      keyValueOrientation[k+1]=' + keyValueOrientation[k+1].toString() + '\\n');\\newline            }\\newline            k++; // update index to match latest key, keyValuePosition, keyValueOrientation\\newline\\newline            // check animated parameter:  set true if any of moves are tracking moves\\newline            if (!animated)  animated = shots[i].moves[j].tracking; // once true, remains true\\newline         // tracePrint ('shots[' + i + '].moves[' + j + '].tracking=' + shots[i].moves[j].tracking + ', animated=' + animated);\\newline\\newline            // intermediate trace\\newline            tracePrint ('                key=' + key);\\newline            tracePrint ('   keyValuePosition=' + keyValuePosition);\\newline            tracePrint ('keyValueOrientation=' + keyValueOrientation);\\newline            tracePrint ('- ' + shots[i].moves[j].description);\\newline        }\\newline    }\\newline    tracePrint ('                key=' + key);\\newline    tracePrint ('   keyValuePosition=' + keyValuePosition);\\newline    tracePrint ('keyValueOrientation=' + keyValueOrientation);\\newline    if (key.length != keyValuePosition.length)\\newline    {\\newline      alwaysPrint ('warning: internal error during array construction, ' +\\newline                  'key.length=' + key.length + ' must equal ' +\\newline                  'keyValuePosition.length=' + keyValuePosition.length);\\newline    }\\newline    if (key.length != keyValueOrientation.length)\\newline    {\\newline      alwaysPrint ('warning: internal error during array construction, ' +\\newline                  'key.length=' + key.length + ' must equal ' +\\newline                  'keyValueOrientation.length=' + keyValueOrientation.length);\\newline    }\\newline    if (key.length != (shotCount + movesCount))\\newline    {\\newline      alwaysPrint ('warning: internal error during array construction, ' +\\newline                  'key.length=' + key.length + ' must equal ' +\\newline                  '(shotCount + movesCount)=' + (shotCount + movesCount));\\newline    }\\newline    tracePrint ('           animated=' + animated);\\newline    // set node values\\newline    CameraPI.key      = key;\\newline    CameraOI.key      = key;\\newline    CameraPI