ewlinefunction checkShots (eventValue)
ewline{
ewline
tracePrint ('checkShots() method should only occur after initialize() methods in all
other Scripts are complete');
ewline
ewline    // compute totalDuration by summing
durations from contained CameraShot and CameraMovement nodes
ewline    totalDuration=
0;
ewline    shotCount  = shots.length;
ewline    movesCount = 0;
ewline    for
(i = 0; i &lt; shotCount; i++) // shots index
ewline    {
ewline       tracePrint ('shots['
+ i + '].moves.length=' + shots[i].moves.length);
ewline       movesCount   += shots[i].moves.length;
ewline
totalDuration = totalDuration + shots[i].shotDuration;
ewline       if (shots[i].moves.length
== 0)
ewline       {
ewline          alwaysPrint ('warning: CameraShot[' + i + ']['
+ shots[i].description + '] has no contained CameraMove nodes');
ewline       }
ewline
}
ewline    // size checks before proceeding
ewline    if (shotCount == 0)
ewline
{
ewline       alwaysPrint ('warning: no CameraShot nodes found for the shots, nothing
to do!');
ewline       return;
ewline    }
ewline    else if (movesCount == 0)
ewline
{
ewline       alwaysPrint ('warning: no CameraMove nodes found for the shots, nothing
to do!');
ewline       return;
ewline    }
ewline    else if (totalDuration ==
0)
ewline    {
ewline       alwaysPrint ('warning: totalDuration = 0 seconds, nothing
to do!');
ewline       return;
ewline    }
ewline    tracePrint ('number of contained
CameraShot nodes=' + shotCount);
ewline    tracePrint ('number of contained CameraMove
nodes=' + movesCount);
ewline    tracePrint ('totalDuration=' + totalDuration + '
seconds for all shots and moves');
ewline
ewline    // compute interpolators
ewline
var k = 0; // index for latest key, keyValuePosition, keyValueOrientation
ewline
for (i = 0; i &lt; shotCount; i++) // shots index
ewline    {
ewline        if (i==0)
// initial entries
ewline        {
ewline           key[0]                   = 0.0;
// no previous move
ewline           keyValuePosition[0]      = shots[i].initialPosition;
ewline
keyValueOrientation[0]   = shots[i].initialOrientation;
ewline        }
ewline 
else     // new shot repositions, reorients camera as clean break from preceding shot/move
ewline
{
ewline           key[k+1]                 = key[k]; // start from end from previous
move
ewline           keyValuePosition[k+1]    = shots[i].initialPosition;
ewline
keyValueOrientation[k+1] = shots[i].initialOrientation;
ewline           k++;
ewline
}
ewline        tracePrint (shots[i].description);
ewline        tracePrint ('shots[i].moves.length='
+ shots[i].moves.length);
ewline
ewline        for (j = 0; j &lt; shots[i].moves.length;
j++) // moves index
ewline        {
ewline            var durationFloat =      
shots[i].moves[j].duration;  // implicit type conversion from SFTime
ewline     
//  durationFloat = new SFFloat (shots[i].moves[j].duration); // explicit type conversion
from SFTime
ewline            //  tracePrint ('durationFloat=' + durationFloat);
ewline
key[k+1]               = key[k] + (durationFloat / totalDuration);
ewline       
keyValuePosition[k+1]  = shots[i].moves[j].goalPosition;
ewline            if (!animated)
ewline
{
ewline                 keyValueOrientation[k+1] = shots[i].moves[j].goalOrientation;
ewline
}
ewline            else
ewline            {
ewline                // using constructor
SFRotation (SFVec3f fromVector, SFVec3f toVector)
ewline                // see X3D
ECMAScript binding Table 7.18 â€” SFRotation instance creation functions
ewline
ewline
// test if difference vector is zero, if so maintain previous rotation
ewline   
var shotVector = ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).normalize();
ewline
if (shotVector.length() &gt;= 0)
ewline                {
ewline                   
// default view direction is along -Z axis
ewline                    shots[i].moves[j].goalOrientation
= new SFRotation (new SFVec3f (0, 0, 1), shotVector);
ewline                    keyValueOrientation[k+1]
= shots[i].moves[j].goalOrientation;
ewline                }
ewline            
else // note (k &gt; 0)
ewline                {
ewline                    keyValueOrientation[k+1]
= keyValueOrientation[k];  // no change
ewline                }
ewline
ewline 
tracePrint ('shots[' + i + '].moves[' + j + '].goalAimPoint=' + shots[i].moves[j].goalAimPoint.toString());
ewline
tracePrint ('        ViewpointNode.position=' + ViewpointNode.position.toString());
ewline
tracePrint ('          shotVector     delta=' + ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).toString());
ewline
tracePrint ('          shotVector normalize=' + ViewpointNode.position.subtract(shots[i].moves[j].goalAimPoint).normalize().toString());
ewline
tracePrint ('               goalOrientation=' + shots[i].moves[j].goalOrientation.toString());
ewline
tracePrint ('      keyValueOrientation[k+1]=' + keyValueOrientation[k+1].toString()
+ '
');
ewline            }
ewline            k++; // update index to match latest
key, keyValuePosition, keyValueOrientation
ewline
ewline            // check animated
parameter:  set true if any of moves are tracking moves
ewline            if (!animated)
animated = shots[i].moves[j].tracking; // once true, remains true
ewline        
// tracePrint ('shots[' + i + '].moves[' + j + '].tracking=' + shots[i].moves[j].tracking
+ ', animated=' + animated);
ewline
ewline            // intermediate trace
ewline
tracePrint ('                key=' + key);
ewline            tracePrint ('   keyValuePosition='
+ keyValuePosition);
ewline            tracePrint ('keyValueOrientation=' + keyValueOrientation);
ewline
tracePrint ('- ' + shots[i].moves[j].description);
ewline        }
ewline    }
ewline
tracePrint ('                key=' + key);
ewline    tracePrint ('   keyValuePosition='
+ keyValuePosition);
ewline    tracePrint ('keyValueOrientation=' + keyValueOrientation);
ewline
if (key.length != keyValuePosition.length)
ewline    {
ewline      alwaysPrint ('warning:
internal error during array construction, ' +
ewline                  'key.length='
+ key.length + ' must equal ' +
ewline                  'keyValuePosition.length='
+ keyValuePosition.length);
ewline    }
ewline    if (key.length != keyValueOrientation.length)
ewline
{
ewline      alwaysPrint ('warning: internal error during array construction, '
+
ewline                  'key.length=' + key.length + ' must equal ' +
ewline 
'keyValueOrientation.length=' + keyValueOrientation.length);
ewline    }
ewline
if (key.length != (shotCount + movesCount))
ewline    {
ewline      alwaysPrint
('warning: internal error during array construction, ' +
ewline                 
'key.length=' + key.length + ' must equal ' +
ewline                  '(shotCount
+ movesCount)=' + (shotCount + movesCount));
ewline    }
ewline    tracePrint ('
animated=' + animated);
ewline    // set node values
ewline    CameraPI.key    
= key;
ewline    CameraOI.key      = key;
ewline    CameraPI.keyValue = keyValuePosition;
ewline
CameraOI.keyValue = keyValueOrientation;
ewline
ewline    if (!animated) // output
results
ewline    {
ewline        tracePrint ('&lt;PositionInterpolator    DEF=\'CameraPositionInterpolator\'
key=\'' + stripBrackets(CameraPI.key) + '\' keyValue=\'' + stripBrackets(CameraPI.keyValue)
+ '\'/&gt;');
ewline        tracePrint ('&lt;OrientationInterpolator DEF=\'CameraOrientationInterpolator\'
key=\'' + stripBrackets(CameraOI.key) + '\' keyValue=\'' + stripBrackets(CameraOI.keyValue)
+ '\'/&gt;');
ewline    }
ewline    tracePrint ('checkShots() complete');
ewline}
