ewlinefunction
stripBrackets (fieldArray)
ewline{
ewline    // some browsers add brackets to array
output strings, this function strips them
ewline    outputString = '';
ewline  
for (i = 0; i &lt; fieldArray.length; i++)
ewline    {
ewline       outputString +=
fieldArray[i].toString();
ewline       if (i &lt; fieldArray.length - 1) outputString
+= ' ';
ewline    }
ewline    return outputString;
ewline}
