ewlinefunction
set_fraction (eventValue, timestamp) // input event received for inputOnly field
ewline{
ewline
// traceEnabled = false;  // for testing purposes
ewline
ewline   // if Camera is
being animated, immediately recompute interpolator settings
ewline   if (animated)
checkShots (true);
ewline
ewline   // trace progress on console with reduced output
frequency
ewline   if (frameCount == 0)
ewline   {
ewline      alwaysPrint ('Animation
loop commencing, timestamp=' + timestamp);
ewline      startTime      = timestamp;
ewline
priorTraceTime = timestamp;
ewline      alwaysPrint ('shotClock=' + (timestamp -
startTime) + ' seconds, frameCount=' + frameCount + ', fraction=' + eventValue + ',
position=' + ViewpointNode.position.toString() + ', orientation=' + ViewpointNode.orientation.toString());
ewline
ewline
if (animated) // output results
ewline      {
ewline        // TODO how to report
or speed up response?  alwaysPrint ('  aimPoint=' + aimPoint.toString());
ewline
tracePrint ('  &lt;PositionInterpolator    DEF=\'CameraPositionInterpolator\'    key=\''
+ stripBrackets(CameraPI.key) + '\' keyValue=\'' + stripBrackets(CameraPI.keyValue)
+ '\'/&gt;');
ewline        tracePrint ('  &lt;OrientationInterpolator DEF=\'CameraOrientationInterpolator\'
key=\'' + stripBrackets(CameraOI.key) + '\' keyValue=\'' + stripBrackets(CameraOI.keyValue)
+ '\'/&gt;');
ewline      }
ewline   }
ewline   else if ((timestamp - priorTraceTime)
&gt;= 1.0) // 1 second trace interval
ewline   {
ewline      alwaysPrint ('shotClock='
+ (timestamp - startTime) + ' seconds, frameCount=' + frameCount + ', fraction=' +
eventValue + ', position=' + ViewpointNode.position.toString() + ', orientation='
+ ViewpointNode.orientation.toString());
ewline      priorTraceTime = timestamp;
ewline
ewline
if (animated) // output results
ewline      {
ewline        // TODO how to report
or speed up response?  alwaysPrint ('  aimPoint=' + aimPoint.toString());
ewline
tracePrint ('  &lt;PositionInterpolator    DEF=\'CameraPositionInterpolator\'    key=\''
+ stripBrackets(CameraPI.key) + '\' keyValue=\'' + stripBrackets(CameraPI.keyValue)
+ '\'/&gt;');
ewline        alwaysPrint ('  &lt;OrientationInterpolator DEF=\'CameraOrientationInterpolator\'
key=\'' + stripBrackets(CameraOI.key) + '\' keyValue=\'' + stripBrackets(CameraOI.keyValue)
+ '\'/&gt;');
ewline      }
ewline   }
ewline   if (eventValue == 0)
ewline   {
ewline
// note that zero value is not necessarily sent first by TimeSensor, so otherwise
ignored
ewline      frameCount++;
ewline   }
ewline   else if (eventValue == 1)
ewline
{
ewline      alwaysPrint ('shotClock=' + (timestamp - startTime) + ', frameCount='
+ frameCount + ', fraction=' + eventValue + ', position=' + ViewpointNode.position.toString()
+ ', orientation=' + ViewpointNode.orientation.toString());
ewline      if (animated)
// output results
ewline      {
ewline        // TODO how to report or speed up
response?  alwaysPrint ('  aimPoint=' + aimPoint.toString());
ewline      }
ewline
alwaysPrint ('Animation loop complete.');
ewline      // do not unbind the Viewpoint
and NavigationInfo nodes, let that be controlled externally
ewline   }
ewline  
else
ewline   {
ewline      frameCount++;
ewline   }
ewline}
