&quot;+
&quot;			    };
&quot;+
&quot;	    	    } else {
&quot;+
&quot;			    return {
&quot;+
&quot;				    scale : new SFVec3f(1.0,1.0,1.0),
&quot;+
&quot;				    translation : new SFVec3f(1.0,1.0,1.0)
&quot;+
&quot;			    };
&quot;+
&quot;		    }
&quot;+
&quot;	    }
